const menu=document.querySelector(".navigation__list--header"),menuItems=document.querySelectorAll(".navigation__link--header"),hamburger=document.querySelector(".hamburger");function toggleMenu(){menu.classList.contains("show-navigation")?menu.classList.remove("show-navigation"):menu.classList.add("show-navigation")}hamburger.addEventListener("click",toggleMenu),menuItems.forEach(function(e){e.addEventListener("click",toggleMenu)}),document.addEventListener("click",e=>{menu.contains(e.target)||hamburger.contains(e.target)||menu.classList.remove("show-navigation")});